import * as RecipeActions from './recipe.actions';
import { Recipe } from '../recipe.model';

export interface FeautureState {
    recipes: State
}

export interface State {
   recipes: Array<Recipe>
}
  
const initialState = {
    recipes: [
        new Recipe('Burger', 'The biggest burger in the world!', 'https://rtvi.com/upload/iblock/58e/58e23f14e52fe081141c45cb39a56fd4.jpg', [{name: 'Potatoes', amount: 4},{name: 'Cheese', amount: 40}, {name: 'Bacon', amount: 2}]),
        new Recipe('Pizza', 'The most delicious pizza in the world!', 'data:image/jpeg;base64,', [{name: 'Potatoes', amount: 4},{name: 'Cheese', amount: 40}, {name: 'Bacon', amount: 2}]),
        new Recipe('Cesar', 'Tasty Cesar!', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSv7WWuXjx0DUJCqnFOWZeYDgVBKYePI_VFwOoJ9ijUUdhnXVDi&s', [{name: 'Potatoes', amount: 4},{name: 'Cheese', amount: 40}, {name: 'Bacon', amount: 2}]),
        new Recipe('Sushi', 'The best rolls only here!', 'data:image/jpeg;base64,', [{name: 'Potatoes', amount: 4},{name: 'Cheese', amount: 40}, {name: 'Bacon', amount: 2}])
    ]
};

export function RecipeReducers(state = initialState, action: RecipeActions.RecipeActions) {
    switch(action.type) {
        case (RecipeActions.ADD_RECIPE): {
            return {
                ...state,
                recipes: [...state.recipes, action.payload]
            }
        }

        
        default:
            return state;
    }
}